function none_for_guest(){var a=document.getElementsByClassName("none_for_guest");for(i=0;i<a.length;i++){a[i].style.display="none";}}function none_for_noreader(){var a=document.getElementsByClassName("none_for_noreader");for(i=0;i<a.length;i++){a[i].style.display="none";}}function none_for_reader(){var a=document.getElementsByClassName("none_for_reader");for(i=0;i<a.length;i++){a[i].style.display="none";}}function onload(){var a=document.getElementsByClassName("guest-footer");if(a.length){if(a[0].style.display=="block"){none_for_guest();console.log("ゲスト");}else{a=document.getElementsByClassName("hatena-follow-button subscribing");if(a.length){none_for_reader();console.log("読者登録済み");}else{none_for_noreader();console.log("読者未登録");}}}else{console.log("モバイル");}}window.addEventListener("load",onload,false);
